import React from "react";
import { Switch, Route } from "react-router-dom";
import ActivationEmail from "./auth/ActivationEmail";
import Login from "./auth/Login";
import NotFound from "../utils/NotFound/NotFound.js"
import Register from "./auth/Register";
import { useSelector } from "react-redux"
import ForgatePassword from "./auth/ForgatePassword";

const Body = () => {
  const auth = useSelector(state => state.auth)
  const { isLogged } = auth
  return (
    <section>
      <Switch>
        <Route exact path="/login" component={isLogged ? NotFound : Login} />
        <Route exact path="/register" component={isLogged ? NotFound : Register} />
        <Route exact path="/forgot_password" component={isLogged ? NotFound : ForgatePassword} />
        <Route
          exact
          path="/user/activate/:activation_token"
          component={ActivationEmail}
        />
      </Switch>
    </section>
  );
};

export default Body;
