import React, { useState } from "react";
import axios from "axios";
import { Button, Card, CardContent, Grow, TextField } from "@material-ui/core";
import { Link, useHistory } from "react-router-dom";
import {
  showErrMsg,
  showSuccessMsg,
} from "../../utils/notification/Notification.js";
import { dispatchLogin } from "../../../redux/actions/authAction.js";
import { useDispatch } from "react-redux";
import classes from "./auth.module.css";

const initialState = {
  email: "",
  password: "",
  err: "",
  success: "",
};

const Login = () => {
  const [user, setUser] = useState(initialState);
  const dispatch = useDispatch();
  const history = useHistory();

  const { email, password, err, success } = user;

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value, err: "", success: "" });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/user/login", { email, password });
      setUser({ ...user, err: "", success: res.data.msg });

      localStorage.setItem("firstLogin", true);
      dispatch(dispatchLogin());
      history.push("/about_me");
    } catch (err) {
      err.response.data.msg &&
        setUser({ ...user, err: err.response.data.msg, success: "" });
    }
  };
  return (
    <div className={classes.login_page}>
    <Grow in>
    <Card style={{ marginTop: "30px" }} variant="outlined">
        <CardContent>
          <h2 style={{textAlign: "center"}}>Login</h2>

          {err && showErrMsg(err)}
          {success && showSuccessMsg(success)}

          <form onSubmit={handleSubmit}>
            <div style={{ marginTop: "13px" }}>
              <TextField
                type="email"
                label="Enter email"
                fullWidth
                name="email"
                value={email}
                variant="outlined"
                onChange={handleChangeInput}
                required
              />
            </div>
            <div style={{ marginTop: "20px" }}>
              <TextField
                type="password"
                label="Enter password"
                name="password"
                value={password}
                onChange={handleChangeInput}
                fullWidth
                variant="outlined"
                required
              />
            </div>
            <div className={classes.row}>
              <Button
                variant="outlined"
                color="primary"
                type="submit"
                style={{ marginTop: "14px" }}
                size="large"
              >
                Login
              </Button>
              <Link to="/forgot_password">Forgot your password</Link>
            </div>
          </form>
          <div style={{ marginTop: "15px" }}>
            <p>
              New Customer? <Link to="/register">Register</Link>
            </p>
          </div>
          <hr></hr>
        </CardContent>
      </Card>
    </Grow>
    </div>
  );
};

export default Login;
