import { makeStyles } from "@material-ui/core/styles";

export default makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    color:"white"
  },
  HomeLink: {
      color: "white",
      textDecoration: "none",
  },
  avatar: {
    width: "38px",
    height: "35px",
    transform: "translateY(10px)",
    borderRadius: "50%"
  }
}));
