import { Container, Grid, Paper, Grow, Card, CardContent, Button } from "@material-ui/core";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { isLength, isMatch } from "../../utils/validation/Validation.js";
import {
  showErrMsg,
  showSuccessMsg,
} from "../../utils/notification/Notification.js";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCamera } from "@fortawesome/free-solid-svg-icons";
import classes from "./profile.module.css";

const initialState = {
  name: "",
  password: "",
  cf_password: "",
  err: "",
  success: "",
};
const Profile = () => {
  const auth = useSelector((state) => state.auth);
  const token = useSelector((state) => state.token);

  const users = useSelector((state) => state.users);

  const { user, isAdmin } = auth;
  const [data, setData] = useState(initialState);
  const { name, password, cf_password, err, success } = data;

  const [avatar, setAvatar] = useState(false);
  const [loading, setLoading] = useState(false);
  const [callback, setCallback] = useState(false);

  const dispatch = useDispatch();
  return (
    <>
      <Grow in>
        <Container>
          <Card >
          <CardContent>
          <Grid container spacing={3}>
              <Grid item md={4} xs={12} className={classes.left_grid}>
                <h2 style={{ textAlign: "center" }}>
                  {isAdmin ? "Admin profile" : "User profiles"}
                </h2>
                <div className={classes.avatar}>
                  <img src={avatar ? avatar : user.avatar} alt="" />
                  <span>
                    <FontAwesomeIcon icon={faCamera} />
                    <p>Change</p>
                    <input type="file" name="file" id="file_up" />
                  </span>
                </div>
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <input
                    type="text"
                    name="name"
                    id="name"
                    defaultValue={user.name}
                    placeholder="Your name"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    name="email"
                    id="email"
                    defaultValue={user.email}
                    placeholder="Your email address"
                    disabled
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="password">New Password</label>
                  <input
                    type="password"
                    name="password"
                    id="password"
                    placeholder="Your password"
                    value={password}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="cf_password">Confirm New Password</label>
                  <input
                    type="password"
                    name="cf_password"
                    id="cf_password"
                    placeholder="Confirm password"
                    value={cf_password}
                  />
                </div>

                <div>
                  <em style={{ color: "crimson" }}>
                    * If you update your password here, you will not be able to
                    login quickly using google and facebook.
                  </em>
                </div>
                <div style={{marginTop: "18px"}}>
                  <Button variant="contained" color="primary" disabled={loading} size="large">Update</Button>
                </div>
              </Grid>
              <Grid item md={8} sm={12}>
              <h2 style={{ textAlign: "center" }}>
                  {isAdmin ? "Users" : "MyOrders"}
                </h2>
              </Grid>
            </Grid>
          </CardContent>
          </Card>
        </Container>
      </Grow>
    </>
  );
};

export default Profile;
